mapper.select:
update movie_info set category_id = #{category_id},
					  movie_title = #{movie_title}, 
					  movie_content = #{movie_content},
					  movie_img = #{movie_img},
					  Img = #{Img}
 where movie_id = #{movie_id}

mapper.update:
UPDATE movie_info SET
movie_views = movie_views+1
WHERE movie_id = #{movie_id}
